package net.cytonic.cytosis.playerlist;

import lombok.Getter;
import net.minestom.server.network.packet.server.play.PlayerInfoUpdatePacket;

import java.util.List;

/**
 * An enum holding data about player list favicons
 */
@Getter
public enum PlayerListFavicon {
    /**
     * A red square
     */
    RED(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * An orange square
     */
    ORANGE(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * A yellow square
     */
    YELLOW(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * A green square
     */
    GREEN(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * A blue square
     */
    BLUE(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * A purple square
     */
    PURPLE(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * A white square
     */
    WHITE(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * A black square
     */
    BLACK(new PlayerInfoUpdatePacket.Property("textures", "", "")),
    /**
     * A grey square
     */
    GREY(new PlayerInfoUpdatePacket.Property("textures", "", ""));

    private final PlayerInfoUpdatePacket.Property property;

    /**
     * Creates a new player list favicon
     *
     * @param property the texture data
     */
    PlayerListFavicon(PlayerInfoUpdatePacket.Property property) {
        this.property = property;
    }

    /**
     * Gets the property in list form
     *
     * @return the property in list form
     */
    public List<PlayerInfoUpdatePacket.Property> property() {
        return List.of(property);
    }
}
